INSERT INTO movies (movie_id, title, origin_name, type, description, thumbnail, quality, lang, status, view, country_id, release_year, is_hot, is_new, is_active, created_at, trailer_url) VALUES
                                                                                                                                                                                               (1, 'All of Us Are Dead', 'All of Us Are Dead', 'SERIES', 'The story revolves around a group of high school students who are trapped in their school during a zombie outbreak, fighting not only the undead but also human greed and jealousy.', 'https://img.ophim.live/uploads/movies/ngoi-truong-xac-song-thumb.jpg', 'HD', 'VIETSUB', 'COMPLETED', 1000, 1, 2021, true, false, true, '2025-05-05 10:00:00', ''),
                                                                                                                                                                                               (2, 'Violent Night', 'Violent Night', 'SINGLE', 'When a team of mercenaries breaks into a wealthy family’s estate on Christmas Eve, taking everyone hostage, they aren’t prepared for a surprise combatant: Santa Claus is on the grounds, and he’s about to show why this Nick is no saint.', 'https://img.ophim.live/uploads/movies/dem-hung-tan-thumb.jpg', 'HD', 'VIETSUB', 'COMPLETED', 800, 2, 2022, true, true, true, '2025-05-05 10:00:00', ''),
                                                                                                                                                                                               (3, 'One Piece Film: Gold', 'One Piece Film: Gold', 'SINGLE', 'The Straw Hat Pirates continue their adventure in Gran Tesoro, the world’s greatest entertainment city, facing off against the “Golden Emperor” Guild Tesoro. What does he want, and what will Luffy’s crew do?', 'https://img.ophim.live/uploads/movies/vua-hai-tac-thanh-pho-vang-thumb.jpg', 'HD', 'VIETSUB', 'COMPLETED', 600, 3, 2016, false, false, true, '2025-05-05 10:00:00', ''),
                                                                                                                                                                                               (4, 'One Piece: Heart of Gold', 'One Piece: Heart of Gold', 'SINGLE', 'The story follows Myskina Olga, who is hunted by both the World Government and a man named Mad Treasure because she knows the location of the Pure Gold. She teams up with the Straw Hat Pirates to reach Alchemi Island.', 'https://img.ophim.live/uploads/movies/vua-hai-tac-trai-tim-vang-thumb.jpg', 'HD', 'VIETSUB', 'COMPLETED', 550, 3, 2016, false, false, true, '2025-05-05 10:00:00', ''),
                                                                                                                                                                                               (5, 'Doraemon: Nobita''s Chronicle of the Moon Exploration', 'Doraemon: Nobita''s Chronicle of the Moon Exploration', 'SINGLE', 'Nobita sees a yellow moon and tells his class about it, but they laugh at him. Doraemon gives him the “Club Club Club” badge, and they discover life on the moon.', 'https://img.ophim.live/uploads/movies/doraemon-nobita-va-mat-trang-phieu-luu-ky-thumb.jpg', 'HD', 'VIETSUB', 'COMPLETED', 500, 3, 2019, false, false, true, '2025-05-05 10:00:00', ''),
                                                                                                                                                                                               (6, 'Weak Hero Class 1', 'Weak Hero Class 1', 'SERIES', 'Yeon Shi-Eun, a top student who appears weak but uses his intelligence, tools, and psychology to fight against violence inside and outside his school.', 'https://img.ophim.live/uploads/movies/nguoi-hung-yeu-duoi-thumb.jpg', 'HD', 'VIETSUB', 'COMPLETED', 1200, 1, 2022, true, true, true, '2025-05-05 10:00:00', ''),
                                                                                                                                                                                               (7, 'The Golden Spoon', 'The Golden Spoon', 'SERIES', 'Based on the webcomic “Geumsujeo,” the drama follows Lee Seung Cheon, a boy born into a poor family, who swaps fates with a friend from a wealthy family using a magical golden spoon.', 'https://img.ophim.live/uploads/movies/gold-spoon-thumb.jpg', 'HD', 'VIETSUB', 'COMPLETED', 1500, 1, 2022, true, true, true, '2025-05-05 10:00:00', '');
